chrome.action.onClicked.addListener(e=>{e.id&&chrome.tabs.sendMessage(e.id,{action:"toggleAssistant"})});chrome.runtime.onInstalled.addListener(e=>{e.reason==="install"&&chrome.storage.sync.set({apiKey:"",modelName:"gpt-3.5-turbo",enableAutoDisplay:!0})});chrome.runtime.onInstalled.addListener(()=>{chrome.contextMenus.create({id:"askAi",title:"使用 AI 助手分析选中内容",contexts:["selection"]})});chrome.contextMenus.onClicked.addListener((e,t)=>{e.menuItemId==="askAi"&&(t!=null&&t.id)&&chrome.tabs.sendMessage(t.id,{action:"analyzeText",text:e.selectionText})});
